mapper.select:
SELECT p.pro_id, p.pro_name, r.room_id, r.room_name, r.room_price, r.min_cnt, r.max_cnt, re.reservation_id, re.user_id, re.user_cnt, re.start_dt, re.end_dt, re.status, ri.rfname AS room_img_fname FROM product p JOIN room r ON p.pro_id = r.pro_id LEFT JOIN ( SELECT room_id, MIN(reservation_id) AS reservation_id FROM reservation GROUP BY room_id ) min_res ON r.room_id = min_res.room_id LEFT JOIN reservation re ON min_res.room_id = re.room_id AND min_res.reservation_id = re.reservation_id LEFT JOIN room_img ri ON r.room_id = ri.room_id WHERE p.PRO_ID = #{pro_id}
AND (re.start_dt IS NULL OR re.end_dt IS NULL OR (TO_DATE(#{start_dt}, 'YYYYMMDDHH24') >= re.end_dt OR TO_DATE(#{end_dt}, 'YYYYMMDDHH24') <= re.start_dt))
AND (r.MIN_CNT <= #{user_cnt} AND r.MAX_CNT >= #{user_cnt})
